mapper.select:
select

from
(
(
(
tb_student_class sc join tb_student s on sc.student_code = s.code and (sc.status = 1 or sc.status = 2)
)
join (select * from tb_order where accept_no = sc.order_no) o on o.accept_no = sc.order_no
)
join tb_class c on c.code = sc.class_code
)



      and m.user_name = #{member}



        and s.code = #{student}


        and c.end_date < date_format(now(), '%Y-%m-%d')
